import React, { useEffect , useState } from 'react'
import { useNavigation, useTheme  } from '@react-navigation/native';
import './Temps.css';
import { useSelector , useDispatch } from 'react-redux';
import { requestTemps } from '../Redux/actions'
import store from '../index'
import _ from 'underscore';


function Temps() {
    
    const [hasInput, updateInput] = useState(false);
    const [index, updateIndex] = useState(0);

    const main = useSelector(state => state.requestTemps.main, _.isEqual);
    const isPending = useSelector(state => state.requestTemps.isPending, _.isEqual);

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(requestTemps());
    }, [dispatch]);
    
    const updateInfo = (i) => {
        updateInput(true);
        if ( i === 0 )
          updateIndex(0);
        else if (i === 1) 
          updateIndex(1);  
        else if (i === 2)
          updateIndex(2);
      }

    console.log(index)
    console.log(main)
    const navigation = useNavigation();

    return(

        <div className='App'> 
        <h1> <button className='botao' title="Next Screen" onClick={() => navigation.navigate('Details')}> Tela 2 </button></h1>
        <h2>
           <button className='botao' onClick={() => updateInfo(0)}> Cidade 1</button>
           <button className='botao' onClick={() => updateInfo(1)}> Cidade 2</button>
           <button className='botao' onClick={() => updateInfo(2)}> Cidade 3</button>
        </h2>
      { 
        hasInput === 'true' ?
        (<div className='card'>
        <p>Local: {main[index].data.name}</p>
        <p>Temperatura Atual: {main[index].data.main.temp}</p>
        <p>Temperatura Máxima: {main[index].data.main.temp_max}</p>
        <p>Temperatura Mínima: {main[index].data.main.temp_min} </p>
        <p><img src={main[index].data.weather[0].icon} alt='ícone'/></p>
        </div>
        )
        : ( <div className='card'> Escolha uma cidade </div>)
      }

      </div>
    )
    
}

export default Temps
