import React, { useEffect } from 'react';
import '../Containers/styles.css'

const InfoCard = ({ main, index }) => {
   
  const checkMin = useSelector(state => state.checkTempMin, shallowEqual)
  const checkMax = useSelector(state => state.checkTempMax, shallowEqual)

  useEffect(() => {
    compareTemps();
  },[])

  const compareTemps = () => {
    if ( main[index].data.main.temp_min < checkMin.temp)
    {
      dispatch(checkTempMin(main[index].data.main.temp_min, main[index].data.name))
    }
    if (main[index].data.main.temp_max > checkMax.temp){
      dispatch(checkTempMax(main[index].data.main.temp_max, main[index].data.name))
    }
  }

  return (
    <div className='card'>
        <p>Local: {main[index].data.name}</p>
        <p>Temperatura Atual: {main[index].data.main.temp}</p>
        <p>Temperatura Máxima: {main[index].data.main.temp_max}</p>
        <p>Temperatura Mínima: {main[index].data.main.temp_min} </p>
        <p><img src={main[index].data.weather[0].icon} alt='ícone'/></p>
        </div>
  )
}
export default InfoCard;
