import React, { useEffect , useState } from 'react'
import { useNavigation} from '@react-navigation/native';
import './styles.css';
import { useSelector , useDispatch } from 'react-redux';
import { requestTemps, checkTemps } from '../Redux/actions'
import InfoCard from '../Components/InfoCard';


function Temps() {
    
    const [hasInput, updateInput] = useState(false);
    const [index, updateIndex] = useState(0);
    const [usedTemp, updateUsed] = useState([]);

    const main = useSelector(state => state.requestTemps.main);
    const check = useSelector(state => state.checkTemps)
    const dispatch = useDispatch();


    useEffect(() => {
        dispatch(requestTemps());
    }, [dispatch]);
    
   
    const compareTemps = () => {
      if ( main[index].data.temp_min < check.minTemp.temp)
      {
        dispatch(checkTemps(main[index].data.main.temp_min))
      }
    }

    const updateInfo = (i) => {
        updateInput(true);
        if ( i === 0 )   
        { 
          updateIndex(0);
          compareTemps();
        }
        else if (i === 1) 
          updateIndex(1);  
        else if (i === 2)
          updateIndex(2);
    }

    console.log(check)
    const navigation = useNavigation();
    return(

        <div className='App'> 
        <h1> <button className='botao' title="Next Screen" onClick={() => navigation.navigate('Details')}> Tela 2 </button></h1>
        <h2>
           <button className='botao' onClick={() => updateInfo(0)}> Cidade 1</button>
           <button className='botao' onClick={() => updateInfo(1)}> Cidade 2</button>
           <button className='botao' onClick={() => updateInfo(2)}> Cidade 3</button>
        </h2>
      { 
        hasInput ?
        <InfoCard index={ index } main={ main } />
        : ( <div className='card'> Escolha uma cidade </div>)
      }

      </div>
    )
    
}

export default Temps
